//ces.Jim:GraphicsObj:G_TextStuff.c#include	"Diffract_INCs.h"void	DrawDiffractString(unsigned char*  theText){	Point 		penPt,lastPt;	short		i,y;	unsigned short	length;	unsigned char		*theChar;	FontInfo	fInfo;	Boolean	drawFlag;		GetFontInfo(&fInfo);	y = fInfo.ascent + fInfo.leading;	theChar = (unsigned char*)theText;	length = (unsigned short)*theChar++;	for(i = 1 ; i <= length ; i++,theChar++){		GetPen(&lastPt);		switch((char)(*theChar)){			case (char)0xDA:				DrawChar('1');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xDB:				DrawChar('2');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xDC:				DrawChar('3');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xDD:				DrawChar('4');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xDE:				DrawChar('5');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xDF:				DrawChar('6');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xE0:				DrawChar('7');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xF5:				DrawChar('8');				GetPen(&penPt);				drawFlag = true;				break;			case (char)0xE1:				DrawChar('9');				GetPen(&penPt);				drawFlag = true;				break;			default:				DrawChar(*theChar);				drawFlag = false;				break;		}		if(drawFlag){			lastPt.v -= y;			Move(-2,-y);			LineTo(lastPt.h,lastPt.v);			MoveTo(penPt.h,penPt.v);		}	}}				