//rces.Jim:Stereographic:ST_StereoMacros.c#define DRAW_BEAM_SPOT(theSpot,cx,cy)								\{																	\	Rect 	beamRect;												\	short	h,v;													\	if(theSpot->flags & BEAM_MASK){									\		beamRect	= theSpot->spotRect;							\		InsetRect(&beamRect,-2,-2);									\		v = (beamRect.bottom + beamRect.top)/2;						\		h = (beamRect.right + beamRect.left)/2;						\		dm_FrameOval(&beamRect);										\		MoveTo(beamRect.left,cy);									\		LineTo(beamRect.right,cy);									\		MoveTo(cx,beamRect.top);									\		LineTo(cx,beamRect.bottom);									\	}																\}#define DRAW_PIN_MARK(thisSpot,cx,cy)								\{																	\	if(thisSpot->flags & PIN_MASK){									\		MoveTo(cx - 4,cy - 2);										\		LineTo(cx + 4,cy + 2);										\		MoveTo(cx - 4,cy + 2);										\		LineTo(cx + 4,cy - 2);										\	}																\}#define DRAW_PRIME_MARK(thisSpot,cx,cy)								\{																	\	if(thisSpot->flags & PRIME_MASK){								\		if((thisSpot->flags & PIN_MASK) == 0){						\			MoveTo(cx - 2,cy - 4);									\			LineTo(cx + 2,cy + 4);									\			MoveTo(cx - 2,cy + 4);									\			LineTo(cx + 2,cy - 4);									\		}															\	}																\}#define DRAW_DEFECT_SPOT(theSpot,cx,cy)								\{																	\	if(theSpot->flags & DEFECT_MASK){								\		dm_PaintOval(&theSpot->spotRect);								\		MoveTo(cx - 4,cy - 4);										\		LineTo(cx + 4,cy + 4);										\		MoveTo(cx - 4,cy + 4);										\		LineTo(cx + 4,cy - 4);										\	}																\}#define DRAW_LINE_SPOT(theSpot,cx,cy)								\{																	\	if(theSpot->flags & LINE_MASK){									\		Rect	theRect;											\		theRect = theSpot->spotRect;								\		InsetRect(&theRect,-2,-2);									\		dm_PaintRect(&theSpot->spotRect);								\		dm_FrameRect(&theRect);										\	}																\}#define	DRAW_HABIT_PLANE(theSpot)									\{																	\	if(theSpot->flags & HABIT_MASK){									\		Rect	theRect;											\		theRect = theSpot->spotRect;								\		InsetRect(&theRect,-2,-2);									\		dm_PaintRect(&theSpot->spotRect);								\		InvertRect(&theRect);										\	}																\}#define DRAW_PLANE_TRACE(theSpot)									\{																	\	if(theSpot->flags & PLANE_TRACE)								\		PlaneTraces(theSpot);										\}#define	DRAW_SMALL_CIRCLE(theSpot)									\{																	\	if(theSpot->flags & SMALL_CIRCLE)								\		DrawSmallCircle(theSpot,false);								\}																	\		#define	INDEX_IS_PRIME(theIndex,primeFlag)							\{																	\	switch(theIndex){												\		case 0:														\			primeFlag = false;										\			break;													\		case 1:														\			primeFlag = true;										\			break;													\		case 2:														\			primeFlag = false;										\			break;													\		case 3:														\			primeFlag = true;										\			break;													\		case 4:														\			primeFlag = false;										\			break;													\		case 5:														\			primeFlag = true;										\			break;													\		case 6:														\			primeFlag = false;										\			break;													\		case 7:														\		case 11:													\		case 13:													\			primeFlag = true;										\			break;													\		case 8:														\		case 9:														\		case 10:													\		case 12:													\			primeFlag = false;										\			break;													\		default:													\			primeFlag = true;										\			break;													\	}																\}																	\			